mapper.select:
select count(1)
from user
where account = #{account,jdbcType=VARCHAR}

select
,UR.role_id as role
from user U
left join user_role UR on U.id = UR.user_id
where account = #{account,jdbcType=VARCHAR}
and password = #{password,jdbcType=VARCHAR}

mapper.insert:

  SELECT LAST_INSERT_ID()

insert into user (account, password, name, 
  level, create_by, create_time, 
  update_by, update_time, last_login_time
  )
values (#{account,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
  #{level,jdbcType=INTEGER}, #{createBy,jdbcType=INTEGER}, #{createTime,jdbcType=TIMESTAMP}, 
  #{updateBy,jdbcType=INTEGER}, #{updateTime,jdbcType=TIMESTAMP}, #{lastLoginTime,jdbcType=TIMESTAMP}
  )
